import React, { useState } from 'react';
import axios from 'axios';

function TrackProduct() {
  const [productId, setProductId] = useState('');
  const [productDetails, setProductDetails] = useState(null);
  const [error, setError] = useState('');

  const handleTrack = async (e) => {
    e.preventDefault();
    try {
      // Reset state sebelumnya
      setProductDetails(null);
      setError('');

      // Panggil API untuk melacak produk
      const response = await axios.get(`http://localhost:5000/api/products/track/${productId}`);

      // Set detail produk
      setProductDetails(response.data);
    } catch (error) {
      console.error('Tracking Error', error);

      // Set pesan error
      if (error.response) {
        setError(error.response.data.message || 'Gagal melacak produk');
      } else {
        setError('Terjadi kesalahan dalam melacak produk');
      }
    }
  };

  return (
    <div>
      <h2>Lacak Produk</h2>
      <form onSubmit={handleTrack}>
        <input
          type="text"
          value={productId}
          onChange={(e) => setProductId(e.target.value)}
          placeholder="Masukkan ID Produk"
          required
        />
        <button type="submit">Lacak Produk</button>
      </form>

      {/* Tampilkan error jika ada */}
      {error && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          {error}
        </div>
      )}

      {/* Tampilkan detail produk */}
      {productDetails && (
        <div style={{ marginTop: '20px' }}>
          <h3>Detail Produk</h3>
          <table>
            <tbody>
              <tr>
                <td><strong>ID Produk:</strong></td>
                <td>{productDetails.id}</td>
              </tr>
              <tr>
                <td><strong>Nama Produk:</strong></td>
                <td>{productDetails.name}</td>
              </tr>
              <tr>
                <td><strong>Status:</strong></td>
                <td>{productDetails.status}</td>
              </tr>
              <tr>
                <td><strong>Lokasi Terakhir:</strong></td>
                <td>{productDetails.currentLocation}</td>
              </tr>
              <tr>
                <td><strong>Tanggal Produksi:</strong></td>
                <td>{new Date(productDetails.productionDate).toLocaleDateString()}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default TrackProduct;
