import React, { useState } from 'react';
import axios from 'axios';

function UpdateProductStatus() {
  const [productData, setProductData] = useState({
    productId: '',
    newStatus: '',
    location: ''
  });

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const statusOptions = [
    'Diproduksi',
    'Dalam Perjalanan',
    'Sedang Diproses',
    'Diterima',
    'Disimpan',
    'Dijual'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData(prevState => ({
      ...prevState,
      [name]: value
    }));

    // Reset pesan
    setMessage('');
    setError('');
  };

  const handleUpdateStatus = async (e) => {
    e.preventDefault();

    try {
      // Validasi input
      if (!productData.productId || !productData.newStatus) {
        setError('Harap isi semua field');
        return;
      }

      // Kirim update status ke backend
      const response = await axios.put(
        `http://localhost:5000/api/products/update-status/${productData.productId}`, 
        {
          status: productData.newStatus,
          location: productData.location
        }
      );

      // Set pesan sukses
      setMessage(response.data.message || 'Status produk berhasil diperbarui');

      // Reset form
      setProductData({
        productId: '',
        newStatus: '',
        location: ''
      });
    } catch (error) {
      console.error('Update Status Error', error);

      // Set pesan error
      if (error.response) {
        setError(error.response.data.message || 'Gagal memperbarui status');
      } else {
        setError('Terjadi kesalahan dalam memperbarui status');
      }
    }
  };

  return (
    <div>
      <h2>Perbarui Status Produk</h2>
      <form onSubmit={handleUpdateStatus}>
        <input
          type="text"
          name="productId"
          value={productData.productId}
          onChange={handleChange}
          placeholder="ID Produk"
          required
        />

        <select
          name="newStatus"
          value={productData.newStatus}
          onChange={handleChange}
          required
        >
          <option value="">Pilih Status Baru</option>
          {statusOptions.map(status => (
            <option key={status} value={status}>
              {status}
            </option>
          ))}
        </select>

        <input
          type="text"
          name="location"
          value={productData.location}
          onChange={handleChange}
          placeholder="Lokasi (opsional)"
        />

        <button type="submit">Perbarui Status</button>
      </form>

      {/* Tampilkan pesan sukses */}
      {message && (
        <div style={{ color: 'green', marginTop: '10px' }}>
          {message}
        </div>
      )}

      {/* Tampilkan pesan error */}
      {error && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          {error}
        </div>
      )}
    </div>
  );
}

export default UpdateProductStatus;
